import React, { Component, Fragment } from 'react';
import TodoItem from './TodoItem';
import '../styles/Todo.css';
import reactDom from 'react-dom';
// import reactDom from 'react-dom';
class TodoContainer extends Component {

    // add() {
    //         // var el = () => {{<TodoItem/>}};

    //         return document.querySelector(".App").appendChild(<TodoItem/>);
    //         // document.querySelector(".App").innerHTML += <TodoItem/>;
    // }

    componentDidMount() {
        reactDom.createPortal(
            <TodoItem hdVal="Task 1" dcVal="this is my task one"/>,   document.querySelector(".App")
        );
    }

    render() {      
        componentDidMount;
        return (
            <>
              <h1>My Todos</h1>  
              <section id="TodoInsert">
                <div>
                    <p>Name</p>
                    <input type="text" id="inName"/>
                </div>
                <div>
                    <p>Description</p>
                    <input type="text" id="inDesc"/>
                </div>
                {/* <button type="button" onClick={this.add}>Add Todo</button> */}
                {/* <button type="button" onClick = {() => {reactDom.createPortal(
                    <TodoItem />,   document.getElementById("root")
                )}}>Add Todo</button> */}
                <button type="button">Add Todo</button>
              </section>
              {/* <section className="TodoItems"> */}
                  <TodoItem hdVal="Task 1" dcVal="this is my task one"/>
                  <TodoItem hdVal="Task 2" dcVal="this is my task two"/>
                  <TodoItem hdVal="Task 3" dcVal="this is my task three"/>
                  <Fragment>
                    {() => this.add}
                  </Fragment>
              {/* </section> */}
            </>
        )
    }
}

export default TodoContainer;
