import React from 'react';
import TodoItem from './TodoItem';

export const ucItems = React.createContext();

function useContext({items}) {
    return (
        <ucItems.Provider value={items}>
           <TodoItem items={items}/> 
        </ucItems.Provider>
    )
}

export default useContext
