import React, { useEffect, useState } from 'react'
import TodoHeader from './components/TodoHeader';
import TodoItem from './components/TodoItem';
import './styles/Todo.css';

function App() {
  const [Items, setItems] = useState([]);
  const [HeaderInputs, setHeaderInputs] = useState({ name: "", desc: "" });

  const handleChange = (e) => {
      setHeaderInputs({
          ...HeaderInputs,
          [e.target.name]: e.target.value,                   
      })
  }

  const handelSubmit = () => {
      const inN = document.getElementById("name"),
          inD = document.getElementById("desc");
      if(inN.value.length >= 3 && inD.value.length >= 5){                        
          setHeaderInputs({ name: "", desc: "" });
          addItem(HeaderInputs);
          console.log( HeaderInputs );
          localStorage.setItem(`Item${Items.length+1} name`, inN.value);
          localStorage.setItem(`Item${Items.length+1} desc`, inD.value);
      } else{ console.log("Attention msg"); }
  }

  const handelComplete = (e) => {
    let trgt = e.target;    
    if ( trgt.classList.contains("comp") ) {
        trgt.classList.remove("comp");  trgt.classList.add("und");
        trgt.setAttribute("title", "Undo");
        trgt.parentElement.firstChild.children[0].style.color = "gray";
        trgt.parentElement.firstChild.children[0].style.textDecoration = "line-through";
        trgt.parentElement.firstChild.children[1].style.color = "gray";
        trgt.parentElement.firstChild.children[1].style.textDecoration = "line-through";    
    }else{ 
        trgt.classList.remove("und");  trgt.classList.add("comp"); 
        trgt.setAttribute("title", "Complete");
        trgt.parentElement.firstChild.children[0].style.color = "#ff9a8d";
        trgt.parentElement.firstChild.children[0].style.textDecoration = "none";
        trgt.parentElement.firstChild.children[1].style.color = "black";
        trgt.parentElement.firstChild.children[1].style.textDecoration = "none";
    }
  }

  const handelDelete = (e) => {
    let trgt = e.target;
    let pr = document.querySelector(".App");
    for(var i=1; i<=pr.children.length; i++){
        if ( pr.children[i] === trgt.parentElement ){
        localStorage.removeItem(`Item${i-1} name`);  localStorage.removeItem(`Item${i-1} desc`);
        pr.removeChild(trgt.parentElement);
        } else{ console.log('no',i-1, localStorage.getItem(`Item${i-1} name`) , localStorage.getItem(`Item${i-1} desc`) )  }
    }
  }

  const lclgItems = [];
  // const lclgItemsLmt  = 100;
  for(var i=1; i<=77; i++){
    console.log( localStorage.getItem(`Item${i} name`), localStorage.getItem(`Item${i} desc`) );    
    // if( ! localStorage.getItem(`Item${i} name`) ) { break; 
    // } else { lclgItems.push({ id: i, name: localStorage.getItem(`Item${i} name`), desc: localStorage.getItem(`Item${i} desc`) }) }    

    // lclgItems.push({ id: i, name: localStorage.getItem(`Item${i} name`), desc: localStorage.getItem(`Item${i} desc`) });
    lclgItems.push({ name: localStorage.getItem(`Item${i} name`), desc: localStorage.getItem(`Item${i} desc`) });
  }
  const lclgItemsFull = lclgItems.filter( function (lclgItem)  {return lclgItem.name != null} );
  console.log(lclgItems);
  console.log( lclgItemsFull );

  for(var i=0; i<localStorage.length; i++){
    console.log( localStorage.getItem( localStorage.key(i) ) );
  }

  // const [Items, setItems] = useState([    
  //   ...lclgItemsFull,
  //   // ...lclgItems,
  // ]);

  setItems( ...lclgItemsFull );

  const  addItem = (item) => {
    // item.id = Items.length+1;
    setItems([ ...Items, item ]);
  }


  return (
    <div className="App">
      <TodoHeader items={Items} addItem={addItem} chgFun={handleChange} subFun={handelSubmit} inputs={HeaderInputs}/>
      <TodoItem items={Items} compFun={handelComplete} dlFun={handelDelete}/>
    </div>
  )
}

export default App
