import React, { Component, useState } from 'react';
import TodoItem from './TodoItem';
import '../styles/Todo.css';
import reactDom from 'react-dom';

class TodoContainer extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            id: "", name: "", desc: ""
        }
    }

    // const add = () => {
    //     //return this.state.items.push({id: this.state.items.length+1, name: "it4", desc: "description four"});
    //     //setitem({ id: 4, name: "Ahmed",  desc: "four", });
    //     props.items.push({ id: 4, name: "Ahmed",  desc: "four", });
    // }

    handleChange = (e) => {
        this.setState({[e.target.id]: e.target.value})
    }

    handelSubmit = (e) => {
        //e.preventDefault();
        this.setState({id: "", name: "", desc: ""});
        this.props.addItem(this.state);
    }

   render() {              
        return (
            <>
              <h1>My Todos</h1>  
              <section id="TodoInsert">
                {/* <form onSubmit={this.handelSubmit}> */}
                <div>
                    <p>Name</p>
                    <input type="text" id="inName" id="name" onChange={this.handleChange} value={this.state.name}/>
                </div>
                <div>
                    <p>Description</p>
                    <input type="text" id="inDesc" id="desc" onChange={this.handleChange} value={this.state.desc}/>
                </div>         
                {/* <button type="submit">Add Todo</button>     */}
                <button type="button" onClick={this.handelSubmit}>Add Todo</button>    
                {/* </form> */}
              </section>
            </>
        )
   }
}

export default TodoContainer;
