// import logo from './logo.svg';
// import './App.css';
import { Component } from 'react';
import TodoContainer from './components/TodoContainer';
import TodoItem from './components/TodoItem';
import './styles/Todo.css';

class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
        items: [
            {id: 1, name: "Task 1", desc: "this is my task one"},
            {id: 2, name: "Task 2", desc: "this is my task two"},
            {id: 3, name: "Task 3", desc: "this is my task three"},  
        ]
    }
  }


  addItem = (item) => {
    let items = this.state.items;
    items.push(item);
    this.setState({items});
  }


  render() {
  return (
    <div className="App">
      <TodoContainer addItem={this.addItem}/>
      {/* {this.state.items.map(item => <TodoItem key={item.id} hdVal={item.name} dcVal={item.desc}/>)} */}
      <TodoItem items={this.state.items} hdVal={this.state.items.name} dcVal={this.state.items.desc}/>
    </div>
  );
  }
}

export default App;
