import React, { useState } from 'react'
import TodoHeader from './components/TodoHeader';
import TodoItem from './components/TodoItem';
import './styles/Todo.css';

function App() {

  const [Items, setItems] = useState([    
    // {id: 1, name: "Task 1", desc: "this is my task one"},
    // {id: 2, name: "Task 2", desc: "this is my task two"},
    // {id: 3, name: "Task 3", desc: "this is my task three"}, 
  ]);


  const  addItem = (item) => {
    // setItems([ ...Items, item ]);
    setItems([ ...Items, item ]);
  }

  useEffect(() => {
    for(var i=1; i<=localStorage.length; i++){
      setItems([ ...Items, {id: i, name: localStorage.getItem(`Item${i} name`), desc: localStorage.getItem(`Item${i} desc`)} ]);
    }  
  }, []);


  // for(var i=1; i<=localStorage.length; i++){
  //   console.log(
  //     localStorage.getItem(`Item${i} name`), localStorage.getItem(`Item${i} desc`)
  //   );
  // }


  return (
    <div className="App">
      <TodoHeader items={Items} addItem={addItem}/>
      <TodoItem items={Items}/>
    </div>
  )
}

export default App
