import React, { useState } from 'react'

function TodoHeader(props) {
    const {items, addItem} = props;
    const [HeaderInputs, setHeaderInputs] = useState({ id: "", name: "", desc: "" });
     
    const handleChange = (e) => {
        // const pickId = items.length-1;
        setHeaderInputs({
            ...HeaderInputs,
            [e.target.name]: e.target.value,            
            id: items.length+1,            
            // id: (Math.random()+Math.random())*10,
            // id: itln,
            // id: items.length+1*Math.random(),   
        })
    }

    const handelSubmit = () => {
        const inN = document.getElementById("name"),
            inD = document.getElementById("desc");
        if(inN.value.length >= 3 && inD.value.length >= 5){                        
            setHeaderInputs({ id: "", name: "", desc: "" });
            addItem(HeaderInputs);
            console.log( HeaderInputs );
            // localStorage.setItem(`Item${items.length+1} name`, inN.value);
            // localStorage.setItem(`Item${items.length+1} desc`, inD.value);
            localStorage.setItem(`${HeaderInputs.id}`, inN.value);
            localStorage.setItem(`${HeaderInputs.id}`, inD.value);
        } else{ console.log("Attention msg"); }
    }

    return (
        <>
              <h1>My Todos</h1>  
              <section id="TodoInsert">
                <div>
                    <p>Name</p>
                    <input type="text" id="name" name="name" onChange={handleChange} value={HeaderInputs.name}/>
                </div>
                <div>
                    <p>Description</p>
                    <input type="text" id="desc" name="desc" onChange={handleChange} value={HeaderInputs.desc}/>
                </div>         
                <button type="button" onClick={handelSubmit}>Add Todo</button>    
              </section>
        </>
    )
}

export default TodoHeader
