import React, { Component } from 'react';
// import reactDom from 'react-dom';
import '../styles/Todo.css';

/*class TodoItem extends Component {   
    render() {
        return (
    <section className="TodoItems">
        <div>
            <h1>Task 1</h1>,
            <p>this is my task one</p>
        </div>
        <button type="button">Delete</button>
        <button type="button">Complete</button>                
    </section>
        )}
}*/





const TodoItem = (props) => {
    const {items} = props;
    const handelComplete = (e) => {
        let trgt = e.target;
        e.target.parentElement.firstChild.children[0].style.color = "gray";
        e.target.parentElement.firstChild.children[1].style.textDecoration = "line-through";
    }
    const ListItems = items.map(item => {
        return (
            <section className="TodoItems" key={item.id}>
                <div>
                    <h1>{item.name}</h1>
                    <p>{item.desc}</p>
                </div>
                <button type="button">Delete</button>
                <button type="button" onClick={ 
                //     (e) =>{                    
                //          e.target.parentElement.firstChild.children[0].style.color = "gray";
                //          e.target.previousSibling.children[1].style.textDecoration = "line-through";
                // }
                handelComplete
                }>Complete</button>                
            </section>  
        );
    })
    return(
        <> {ListItems} </>
    )
}


export default TodoItem;
