import React, { Component } from 'react';
import TodoItem from './TodoItem';
import '../styles/Todo.css';
import reactDom from 'react-dom';
// function add(){
//     return reactDom.createPortal(
//         <h1>hello</h1>, document.getElementsByClassName("App")[0]
//     )
// }


class TodoContainer extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
            items: [
                {id: 1, name: "Task 1", desc: "this is my task one"},
                {id: 2, name: "Task 2", desc: "this is my task two"},
                {id: 3, name: "Task 3", desc: "this is my task three"},  
            ]
        }
    }
    
    add(){
        this.state.items.push({id: this.state.items.length+1, name: "it4", desc: "description four"});
    }

    render() {              
        return (
            <>
              <h1>My Todos</h1>  
              <section id="TodoInsert">
                <div>
                    <p>Name</p>
                    <input type="text" id="inName"/>
                </div>
                <div>
                    <p>Description</p>
                    <input type="text" id="inDesc"/>
                </div>                
                <button type="button" onClick={() => {
                    this.add();
                    this.state.items.map(item => <TodoItem key={item.id} hdVal={item.name} dcVal={item.desc}/>);
                }}>Add Todo</button>
              </section>
              {this.state.items.map(item => <TodoItem key={item.id} hdVal={item.name} dcVal={item.desc}/>)}
                {/* <TodoItem hdVal="Task 1" dcVal="this is my task one"/>
                <TodoItem hdVal="Task 2" dcVal="this is my task two"/>
                <TodoItem hdVal="Task 3" dcVal="this is my task three"/>  */}
                {/* {add()} */}
            </>
        )
    }
}

export default TodoContainer;
